   t1.onclick=function(){
     var scrollTop=
        document.body.scrollTop
        ||document.documentElement.scrollTop;
     //总时间
     var dura=2000;
    
     var steps=400;
    
     var step=scrollTop/steps
     
     var interval=dura/steps;
    
     var timer=setInterval(function(){
       window.scrollBy(0,-step)
       steps--;
      
        if(steps==0){
        
          clearInterval(timer);
        }
     },interval)
   }

